		setup_distributeheightswithinrow: function (){// cutom function to give all elements within the same row the same height, based on the hightest within that row, Bvl
		    "use strict";

			var		t = 1,
					highest = 0,
					highestarray = [],
					previous,
					size  = ($('.distributeheightswithinrow').length)-1;

			$('.distributeheightswithinrow').css({'min-height': ''});

			$('.distributeheightswithinrow').each(function(i){
				var 	dis = $(this),
						disheight = dis.outerHeight();

				dis.removeClass (function(index,css) {
				    return (css.match(/\bpartofrow-\S+/g) || []).join(' ');
				});

				if(i ===0){//first match, highest is obviousely the height of the only match so far, BvL
					highest = disheight;
				}else {
					if (dis.offset().top != previous.offset().top){
						highestarray.push(highest);
						highest = disheight;
						t= t+1;
					}else{
						if (disheight > highest){
							highest = disheight;
						}
					}
				}

				dis.addClass('partofrow-'+t);
				previous = dis;

				if(i == size){
					highestarray.push(highest);
				}
			});


			$('.distributeheightswithinrow').each(function(){
				var dis = $(this),
					num = dis.attr('class').match(/partofrow-(\d+)/)[1];

				if(dis.css('display')=='table') {
					dis.css('height', highestarray[num-1]);
				}else{
					dis.css('min-height', highestarray[num-1]);
				}
			});

		},
