(function ($) {
  $(document).ready(function() {
    var slideWrapper = $(".field-carousel-items-wrapper"),
      iframes = slideWrapper.find('.embed-player'),
      lazyImages = slideWrapper.find('.slide-image'),
      lazyCounter = 0;

    // POST commands to YouTube or Vimeo API
    function postMessageToPlayer(player, command){
      if (player == null || command == null) return;
      player.contentWindow.postMessage(JSON.stringify(command), "*");
    }

    // When the slide is changing
    function playPauseVideo(slick, control){
      var currentSlide, slideType, startTime, player, video;

      slick.slick('slickPlay');

      currentSlide = slick.find(".slick-current");
      slideType = currentSlide.find('video').length === 0 ? 'image' : 'video';
      player = currentSlide.find("iframe").get(0);
      startTime = currentSlide.data("video-start");

      // YouTube/Vimeo functionality removed as out-of-scope, and changes below
      // may not be supported.
      
      if (slideType === "video") {
        video = currentSlide.find("video").get(0);
        if (video != null) {
          if (control === "play"){
            video.loop = false;
            video.currentTime = 0;
            video.play();
            
            slick.slick('slickPause');
          } else {
            video.currentTime = 0;
            video.pause();
          }
        }
      }
    }

    // Resize player
    function resizePlayer(iframes, ratio) {
      if (!iframes[0]) return;
      var win = $(".field-carousel-items-wrapper"),
        width = win.width(),
        playerWidth,
        height = win.height(),
        playerHeight,
        ratio = ratio || 16/9;

      iframes.each(function(){
        var current = $(this);
        if (width / ratio < height) {
          playerWidth = Math.ceil(height * ratio);
          current.width(playerWidth).height(height).css({
            left: (width - playerWidth) / 2,
             top: 0
            });
        } else {
          playerHeight = Math.ceil(width / ratio);
          current.width(width).height(playerHeight).css({
            left: 0,
            top: (height - playerHeight) / 2
          });
        }
      });
    }


    // DOM Ready
    $(function() {
      // Initialize
      slideWrapper.on("init", function(slick){
        slick = $(slick.currentTarget);
        setTimeout(function(){
          playPauseVideo(slick,"play");
        }, 1000);
        resizePlayer(iframes, 16/9);

        $('video', slick).bind('ended', function() {
          slick.slick('slickPlay');
          slick.slick('slickNext');
        });
      });
      slideWrapper.on("beforeChange", function(event, slick) {
        slick = $(slick.$slider);
        playPauseVideo(slick,"pause");
      });
      slideWrapper.on("afterChange", function(event, slick) {
        slick = $(slick.$slider);
        playPauseVideo(slick,"play");
      });
      slideWrapper.on("lazyLoaded", function(event, slick, image, imageSource) {
        lazyCounter++;
        if (lazyCounter === lazyImages.length){
          lazyImages.addClass('show');
        }
      });

      //start the slider
      slideWrapper.slick({
        autoplay: true,
        autoplaySpeed:4000,
//        lazyLoad:"progressive",
        speed:600,
        dots:true,
      });
    });

    // Resize event
    $(window).on("resize.slickVideoPlayer", function(){  
      resizePlayer(iframes, 16/9);
    });
  });
})(jQuery); 
